import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPenToSquare } from '@fortawesome/free-solid-svg-icons';
import { faTrash } from '@fortawesome/free-solid-svg-icons';

export const Todo = ({ task, deleteTodo, editTodo, toggleComplete, editButtonStyle, deleteButtonStyle }) => {
  return (
    <div>
      <span style={{ textDecoration: task.completed ? "line-through" : "" }}>
        {task.task}
      </span>
      <button style={editButtonStyle} className="edit-button" onClick={editTodo} disabled={task.isEditing || task.completed}>
        <FontAwesomeIcon icon={faPenToSquare} /> Edit
      </button>
      <button style={deleteButtonStyle} className="delete-button" onClick={deleteTodo} disabled={task.isEditing || task.completed}>
        <FontAwesomeIcon icon={faTrash} /> Remove
      </button>
      <button className="complete-button" onClick={toggleComplete} disabled={task.isEditing}>
        {task.completed ? "Uncomplete" : "Complete"}
      </button>
    </div>
  );
};
