import React, { useState } from 'react';

const TodoItem = ({ todo, onComplete, onEdit, onDelete }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [newText, setNewText] = useState(todo.text);

  const handleComplete = () => {
    onComplete(todo.id);
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    onEdit(todo.id, newText);
    setIsEditing(false);
  };

  const handleRemove = () => {
    onDelete(todo.id);
  };

  return (
    <li
      style={{
        textDecoration: todo.completed ? 'line-through' : 'none',
      }}
    >
      {isEditing ? (
        <input
          type="text"
          value={newText}
          onChange={(e) => setNewText(e.target.value)}
          placeholder="Edit todo"
        />
      ) : (
        <span>{todo.text}</span>
      )}
      <button
        onClick={handleComplete}
        disabled={isEditing || todo.completed}
      >
        Complete
      </button>
      <button
        onClick={handleEdit}
        disabled={isEditing || todo.completed}
      >
        Edit
      </button>
      <button
        onClick={handleRemove}
        disabled={isEditing || todo.completed}
      >
        Remove
      </button>
      {isEditing && (
        <button onClick={handleSave}>Save</button>
      )}
    </li>
  );
};

const TodoList = ({ todos, onComplete, onEdit, onDelete }) => {
  if (todos.length === 0) {
    return <p>No todos available. Add a todo to get started!</p>;
  }

  return (
    <ul>
      {todos.map((todo) => (
        <TodoItem
          key={todo.id}
          todo={todo}
          onComplete={onComplete}
          onEdit={onEdit}
          onDelete={onDelete}
        />
      ))}
    </ul>
  );
};

export default TodoList;
